INSERT INTO question_bank (id,subject,topic_key,language,qtype,difficulty,question_json,status,kb_backend,rewrite_mode,llm_model) VALUES ('qb_1767434437638_m2j8','Mathematics','math_m2_5','zh','long',3,'{"question":"考慮極限 \\( \\lim_{x \\to 0} \\frac{\\sin x}{x} \\)。請回答以下問題。","parts":[{"part":"a","question":"使用洛必達法則求出該極限的值。","marks":3,"modelAnswer":"根據洛必達法則，恆等式 \\( \\lim_{x\\to 0} \\frac{\\sin x}{x} = \\lim_{x\\to 0} \\frac{\\cos x}{1} = \\cos(0) = 1 \\)"},{"part":"b","question":"用泰勒級數展開 \\( \\sin x \\)，並求出同樣的極限值。","marks":4,"modelAnswer":"利用泰勒級數，\\( \\sin x = x - \\frac{x^3}{6} + O(x^5) \\)，所以 \\( \\frac{\\sin x}{x} = 1 - \\frac{x^2}{6} + O(x^4) \\)。當 \\( x \\to 0 \\) 時，\\( \\lim_{x \\to 0} \\frac{\\sin x}{x} = 1 \\)。"}],"topic":"math_m2_5","totalMarks":10}','ready','none',0,'gpt-4o-mini');