INSERT INTO question_bank (id, topic_key, qtype, year, paper, question_number, content, answer, explanation, difficulty, calibrated_difficulty, skill_node_id) VALUES ('easy-atomic-5-d1-1767685696455-t5wfvc', 'physics-learn', 'mc', 2025, 'generated', 'easy', '{"question":"根據德布羅意（de Broglie）關係，若一粒子的動量增加，則其德布羅意波長會如何改變？","options":["A. 波長增加","B. 波長減少","C. 波長不變，與動量無關","D. 粒子沒有波長"]}', 'B', '德布羅意關係為 λ = h / p（λ 為波長，h 為普朗克常數，p 為動量）。由此可見波長與動量成反比，當動量增加時，德布羅意波長會減少。', 1, 1, 'atomic-5');
INSERT INTO question_bank (id, topic_key, qtype, year, paper, question_number, content, answer, explanation, difficulty, calibrated_difficulty, skill_node_id) VALUES ('easy-atomic-5-d1-1767685696455-d2iwt2', 'physics-learn', 'fill_blank', 2025, 'generated', 'easy', '{"question":"德布羅意提出每個粒子都具有波動性，粒子的波長稱為 _____（其公式為 λ = h / p，其中 h 是普朗克常數，p 是粒子的動量）。","blanks":["德布羅意波長"]}', '德布羅意波長', '「德布羅意波長」是指物質波（matter wave）的波長。德布羅意假設任何具有動量 p 的粒子都可表示為波，其波長 λ 與普朗克常數 h 及動量 p 的關係為 λ = h / p（其中 h ≈ 6.63×10^−34 J·s，p = mv）。這說明粒子在某些條件下會表現出波動性。', 1, 1, 'atomic-5');
INSERT INTO question_bank (id, topic_key, qtype, year, paper, question_number, content, answer, explanation, difficulty, calibrated_difficulty, skill_node_id) VALUES ('easy-atomic-5-d1-1767685696455-969xc6', 'physics-learn', 'short', 2025, 'generated', 'easy', '{"question":"簡述德布羅意波長的定義，並寫出非相對論情況下的公式。"}', '德布羅意波長是與粒子動量相關的波長，公式為 λ = h/p；對非相對論粒子可寫成 λ = h/(mv)。', '德布羅意假設所有物質粒子具有波動性，其波長與動量成反比，由普朗克常數 h 與粒子動量 p 決定。對速度遠小於光速的粒子，動量 p = mv，可代入得到 λ = h/(mv)。', 1, 1, 'atomic-5');
INSERT INTO question_bank (id, topic_key, qtype, year, paper, question_number, content, answer, explanation, difficulty, calibrated_difficulty, skill_node_id) VALUES ('easy-atomic-5-d1-1767685696455-vugjeu', 'physics-learn', 'mc', 2025, 'generated', 'easy', '{"question":"下列哪一項決定一個物質粒子的 de Broglie 波長（物質波）？","options":["A. 只由粒子的質量決定","B. 由粒子的動量決定","C. 由粒子的電荷決定","D. 由光的波長決定"]}', 'B', '根據 de Broglie 關係，粒子的波長 λ = h / p（h 為普朗克常數，p 為動量）。因此物質波的波長由粒子的動量決定（在非相對論情況下 p ≈ mv，質量與速度共同影響動量，但不是單獨由質量決定）。電荷或光的波長與物質粒子的 de Broglie 波長無直接關係。', 1, 1, 'atomic-5');
INSERT INTO question_bank (id, topic_key, qtype, year, paper, question_number, content, answer, explanation, difficulty, calibrated_difficulty, skill_node_id) VALUES ('easy-atomic-5-d1-1767685696455-g1o1k1', 'physics-learn', 'fill_blank', 2025, 'generated', 'easy', '{"question":"根據德布羅意波長公式，波長 λ 等於普朗克常數 h 除以粒子動量 p，寫成 λ = _____.（其中 h 為普朗克常數，p 為粒子動量）","blanks":["h/p"]}', 'h/p', '德布羅意假說指出所有粒子具有波動性，其波長稱為德布羅意波長，給定公式為 λ = h / p，其中 h 為普朗克常數（約 6.63×10^-34 J·s），p 為粒子動量。在非相對論情況下，p = mv。', 1, 1, 'atomic-5');
INSERT INTO question_bank (id, topic_key, qtype, year, paper, question_number, content, answer, explanation, difficulty, calibrated_difficulty, skill_node_id) VALUES ('easy-atomic-5-d2-1767685696455-6kn01t', 'physics-learn', 'mc', 2025, 'generated', 'easy', '{"question":"一個電子由靜止經由一個100 V的電勢差被加速。若忽略相對論效應，該電子的德布羅意波長約為多少？（提示：電子經V伏特加速的德布羅意波長可近似為 λ(nm) ≈ 1.226/√V）","options":["A. 1.23×10^-10 m","B. 1.23×10^-8 m","C. 1.23×10^-12 m","D. 1.23×10^-6 m"]}', 'A', '使用提示的近似公式 λ(nm) ≈ 1.226/√V，代入 V = 100 V 得 λ ≈ 1.226/√100 = 1.226/10 = 0.1226 nm = 1.226×10^-10 m，約為 1.23×10^-10 m，因此正確答案為 A。', 2, 2, 'atomic-5');
INSERT INTO question_bank (id, topic_key, qtype, year, paper, question_number, content, answer, explanation, difficulty, calibrated_difficulty, skill_node_id) VALUES ('easy-atomic-5-d2-1767685696455-86h9j1', 'physics-learn', 'fill_blank', 2025, 'generated', 'easy', '{"question":"一個電子以速度 1.0×10^6 m/s 運動。已知電子質量 m = 9.11×10^-31 kg，普朗克常數 h = 6.63×10^-34 J·s。按德布羅意關係 λ = h/(mv)，計算該電子的德布羅意波長 λ = _____ m（以科學記數法表示）。","blanks":["7.3×10^-10 m"]}', '7.3×10^-10 m', '由德布羅意關係 λ = h/(mv)。將數值代入：λ = 6.63×10^-34 J·s ÷ (9.11×10^-31 kg × 1.0×10^6 m/s) = 6.63×10^-34 ÷ 9.11×10^-25 ≈ 7.28×10^-10 m。四捨五入到兩有效數字得約 7.3×10^-10 m。', 2, 2, 'atomic-5');
INSERT INTO question_bank (id, topic_key, qtype, year, paper, question_number, content, answer, explanation, difficulty, calibrated_difficulty, skill_node_id) VALUES ('easy-atomic-5-d2-1767685696455-p28nre', 'physics-learn', 'short', 2025, 'generated', 'easy', '{"question":"一個電子以速度 2.2×10^6 m/s 在真空中運動。求此電子的德布羅意波長（取 Planck 常數 h = 6.63×10^-34 J·s，電子質量 m = 9.11×10^-31 kg）。"}', '約 3.31×10^-10 m（約 0.33 nm）。', '由德布羅意關係 λ = h / p = h / (mv)。代入數值 λ = 6.63×10^-34 / (9.11×10^-31×2.2×10^6) ≈ 3.31×10^-10 m，約 0.33 nm。', 2, 2, 'atomic-5');
